mapper.select:
SELECT files.id, file_name, modifiable_name, file_path, picture.id as picture_id, love, `display`, author, width, height, mp, file_size, cover, create_time FROM files LEFT JOIN picture ON files.id = picture.files_id WHERE type = 3 AND deleted = 1
and parent_id = #{picture_id}
and love = #{love}
and display = #{display}
and author = #{author}
ORDER BY files.id LIMIT #{offset},#{limit}

SELECT count(*) FROM files
LEFT JOIN picture ON files.id = picture.files_id
WHERE type = 3 AND deleted = 1

    and parent_id = #{picture_id}


    and love = #{love}


    and display = #{display}


    and author = #{author}

SELECT files.id, file_name, modifiable_name, file_path, picture.id AS picture_id, love, `display`, author, width, height, mp, file_size, cover, create_time FROM files INNER JOIN picture ON files.id = picture.files_id WHERE type = 3 AND deleted = 1 AND is_folder = 2
and love = #{love}
and display = #{display}
ORDER BY picture.create_time LIMIT #{offset},#{limit}

SELECT count(*) FROM files
LEFT JOIN picture ON files.id = picture.files_id
WHERE type = 3 AND deleted = 1 AND is_folder = 2

    and love = #{love}


    and display = #{display}
