mapper.select:
SELECT
    series.id,
    series.author,
    series.over_status,
    series.`status`,
    series.love,
    series.`profile`,
    series.last_read_time,
    series.num,
    modifiable_name as `name`,
    series.cover,
    series.files_id
FROM
    series
INNER JOIN files ON series.files_id = files.id AND files.deleted = 1
WHERE series.deleted = 1

    and love = #{love}



    and `modifiable_name` LIKE #{pattern}


    and series.`status` = #{status}


    and over_status = #{overStatus}




            ORDER BY modifiable_name ${sortOrder}


            ORDER BY series.last_read_time ${sortOrder}


            ORDER BY series.add_time ${sortOrder}



LIMIT #{offset},#{limit}

SELECT count(*) FROM series
INNER JOIN files ON series.files_id = files.id AND files.deleted = 1
WHERE series.deleted = 1

    and love = #{love}



    and `modifiable_name` LIKE #{pattern}


    and series.`status` = #{status}


    and over_status = #{overStatus}
